package com.abdurashidov.service_jobs_workmanagerimport android.content.Contextimport android.util.Logimport androidx.work.Workerimport androidx.work.WorkerParametersclass UploadWork(context: Context, workerParameters: WorkerParameters) :    Worker(context, workerParameters) {    private val TAG = "UploadWork"    override fun doWork(): Result {        uploadImage()        return Result.success()    }    private fun uploadImage() {        Log.d(TAG, "uploadImage: ")    }}